def main():
    hook = MemoryHooker.MemoryHooker(pid)
    keyboard = MemoryHooker.Keyboard()
    while 1:
        value = hook.readInt32(address)
        x = (value-6)//8
        i = (x-13)//9
        if (i > len(inputs)):
            ## Dead
            keyboard.releaseKey(0x11)  # Release W
        else:
            print(value, x, i, inputs[i])
            if inputs[i]:
                keyboard.pressKey(0x11) # Press W
            else:
                keyboard.releaseKey(0x11)  # Release W
                ##hook.sendKeys("w")

        time.sleep(1/fps)

    hook.close()

if __name__ == "__main__":
    try:
        main()
    except KeyboardInterrupt:
        pass